mapper.insert:
insert into easybuy_product (name, description, price, 
  stock, categoryLevel1Id, categoryLevel2Id, 
  categoryLevel3Id, fileName, isDelete
  )
values (#{name,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{price,jdbcType=REAL}, 
  #{stock,jdbcType=INTEGER}, #{categorylevel1id,jdbcType=INTEGER}, #{categorylevel2id,jdbcType=INTEGER}, 
  #{categorylevel3id,jdbcType=INTEGER}, #{filename,jdbcType=VARCHAR}, #{isdelete,jdbcType=INTEGER}
  )

mapper.select:
select

from easybuy_product
where   categoryLevel1Id = #{id,jdbcType=INTEGER}
    or  categoryLevel2Id = #{id,jdbcType=INTEGER}
    or categoryLevel3Id = #{id,jdbcType=INTEGER}
   or name LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')
